mapper.insert:
INSERT INTO member 
(userid, userpw, username, email, regdate, updatedate)
VALUES (
#{userid},#{userpw},#{username}
,#{email},SYSDATE,SYSDATE
)

mapper.update:
update member set 
userpw = #{userpw}
,username = #{username}
,email = #{email}
,regdate = #{regdate}
,updatedate = #{updatedate}
where userid = #{userid}
